mapper.update:

update menu
set menuname = #{menuName},
    menudesc = #{menuDesc},
    menuprice = #{menuPrice},
   	isSpecial = #{isSpecial},
   	isChange = #{isChange}


	, unitNo = #{unitNo}



	, menuImg = #{menuImg}


where menuNo = #{menuNo}

mapper.select:

select max(unitNo) as unit_No
from setunit
where storeno = #{storeNo}

mapper.update:

update setunit
set unitName = #{unitName}
where unitNo = #{unitNo}

mapper.select:

select count(*)
from menu
where unitNo = #{unitNo}

mapper.update:

update menu
set unitNo = null
where unitNo = #{unitNo}
